import React, { useRef, useEffect, useState } from 'react';
import { db } from './firebase';
import { collection, addDoc, onSnapshot, setDoc, doc, getDoc, query, where } from 'firebase/firestore';
import { useHistory } from 'react-router-dom';

const VideoCall = () => {
  const history = useHistory();
  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);
  const [pc, setPc] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [incomingCall, setIncomingCall] = useState(null);

  useEffect(() => {
    const checkAuth = async () => {
      const loggedInUser = localStorage.getItem('currentUser');
      if (!loggedInUser) {
        alert('Lütfen önce giriş yapın!');
        history.push('/login');
        return;
      }
      const user = JSON.parse(loggedInUser);
      setCurrentUser(user);

      // Kullanıcıyı çevrimiçi olarak işaretle
      const userStatusRef = doc(db, 'userStatus', user.email);
      await setDoc(userStatusRef, { 
        online: true,
        lastSeen: new Date().toISOString(),
        email: user.email
      });

      // Kullanıcı çıkış yaptığında offline yap
      window.addEventListener('beforeunload', async () => {
        await setDoc(userStatusRef, { 
          online: false,
          lastSeen: new Date().toISOString(),
          email: user.email
        });
      });
    };

    checkAuth();

    // Çevrimiçi kullanıcıları dinle
    const q = query(collection(db, 'userStatus'), where('online', '==', true));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const users = [];
      snapshot.forEach((doc) => {
        if (doc.data().email !== currentUser?.email) {
          users.push(doc.data());
        }
      });
      setOnlineUsers(users);
    });

    // Gelen aramaları dinle
    const callsQuery = query(
      collection(db, 'calls'), 
      where('targetUser', '==', currentUser?.email),
      where('status', '==', 'pending')
    );

    const callsUnsubscribe = onSnapshot(callsQuery, (snapshot) => {
      snapshot.forEach((doc) => {
        setIncomingCall({
          id: doc.id,
          ...doc.data()
        });
      });
    });

    return () => {
      unsubscribe();
      callsUnsubscribe();
    };
  }, [currentUser, history]);

  // WebRTC kurulumu
  const setupWebRTC = async () => {
    const servers = {
      iceServers: [{ urls: 'stun:stun.l.google.com:19302' }],
    };

    const peerConnection = new RTCPeerConnection(servers);
    setPc(peerConnection);

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      localVideoRef.current.srcObject = stream;
      stream.getTracks().forEach(track => peerConnection.addTrack(track, stream));
    } catch (error) {
      console.error('Error accessing media devices:', error);
    }

    peerConnection.ontrack = (event) => {
      remoteVideoRef.current.srcObject = event.streams[0];
    };

    return peerConnection;
  };

  const callUser = async (targetUser) => {
    const peerConnection = await setupWebRTC();

    const callDoc = doc(collection(db, 'calls'));
    const offerCandidates = collection(callDoc, 'offerCandidates');
    const answerCandidates = collection(callDoc, 'answerCandidates');

    peerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        addDoc(offerCandidates, event.candidate.toJSON());
      }
    };

    const offerDescription = await peerConnection.createOffer();
    await peerConnection.setLocalDescription(offerDescription);

    await setDoc(callDoc, {
      offer: {
        sdp: offerDescription.sdp,
        type: offerDescription.type,
      },
      createdBy: currentUser.email,
      targetUser: targetUser.email,
      status: 'pending',
      createdAt: new Date().toISOString()
    });

    onSnapshot(callDoc, (snapshot) => {
      const data = snapshot.data();
      if (data?.answer && !peerConnection.currentRemoteDescription) {
        const answerDescription = new RTCSessionDescription(data.answer);
        peerConnection.setRemoteDescription(answerDescription);
      }
    });

    onSnapshot(answerCandidates, (snapshot) => {
      snapshot.docChanges().forEach((change) => {
        if (change.type === 'added') {
          const candidate = new RTCIceCandidate(change.doc.data());
          peerConnection.addIceCandidate(candidate);
        }
      });
    });
  };

  const answerCall = async () => {
    if (!incomingCall) return;

    const peerConnection = await setupWebRTC();
    const callDoc = doc(db, 'calls', incomingCall.id);
    const answerCandidates = collection(callDoc, 'answerCandidates');
    const offerCandidates = collection(callDoc, 'offerCandidates');

    peerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        addDoc(answerCandidates, event.candidate.toJSON());
      }
    };

    const callData = (await getDoc(callDoc)).data();
    const offerDescription = callData.offer;
    await peerConnection.setRemoteDescription(new RTCSessionDescription(offerDescription));

    const answerDescription = await peerConnection.createAnswer();
    await peerConnection.setLocalDescription(answerDescription);

    await setDoc(callDoc, {
      answer: {
        sdp: answerDescription.sdp,
        type: answerDescription.type,
      },
      status: 'answered'
    }, { merge: true });

    onSnapshot(offerCandidates, (snapshot) => {
      snapshot.docChanges().forEach((change) => {
        if (change.type === 'added') {
          const candidate = new RTCIceCandidate(change.doc.data());
          peerConnection.addIceCandidate(candidate);
        }
      });
    });

    setIncomingCall(null);
  };

  return (
    <div>
      <h2>Video Görüşmesi</h2>
      {currentUser && <p>Kullanıcı: {currentUser.email}</p>}

      <div>
        <h3>Çevrimiçi Kullanıcılar</h3>
        <ul>
          {onlineUsers.map((user) => (
            <li key={user.email}>
              {user.email}
              <button onClick={() => callUser(user)}>Ara</button>
            </li>
          ))}
        </ul>
      </div>

      {incomingCall && (
        <div>
          <p>{incomingCall.createdBy} arıyor...</p>
          <button onClick={answerCall}>Cevapla</button>
          <button onClick={() => setIncomingCall(null)}>Reddet</button>
        </div>
      )}

      <div>
        <video ref={localVideoRef} autoPlay muted width="400" />
        <video ref={remoteVideoRef} autoPlay width="400" />
      </div>
    </div>
  );
};

export default VideoCall;
